ValidationRule.errorConditionFormula:
NOT(
    OR(
        /* Exatamente 1 preenchido */
        AND(NOT(ISBLANK(City__c)), ISBLANK(State__c),      ISBLANK(Country__c)),
        AND(ISBLANK(City__c), NOT(ISBLANK(State__c)), ISBLANK(Country__c)),
        AND(ISBLANK(City__c), ISBLANK(State__c), NOT(ISBLANK(Country__c)))
    )
)
